#!/usr/bin/env python3
"""Generate a simple icon if it does not exist."""
from __future__ import annotations

import base64
from pathlib import Path

ICON_BASE64 = (
""
)


def create_simple_icon() -> None:
    icon_path = Path(__file__).with_name("icon.ico")
    if icon_path.exists() and icon_path.stat().st_size > 10:
        return
    data = base64.b64decode(ICON_BASE64.encode("ascii"))
    icon_path.write_bytes(data)
    print(f"✅ تم إنشاء الأيقونة: {icon_path}")


if __name__ == "__main__":
    create_simple_icon()
